// src/components/UserProfile.jsx
import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import useFetch from '../hooks/useFetch';

const UserProfile = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { data: user, loading, error } = useFetch(
    `https://jsonplaceholder.typicode.com/users/${id}`
  );

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;
  if (!user) return <div>User not found.</div>;

  return (
    <div className="UserProfile-profile">
      <img
        src={`https://via.placeholder.com/120?text=${user.name.charAt(0)}`}
        alt={`${user.name}'s avatar`}
        className="UserProfile-avatar"
      />
      <h2>{user.name}</h2>
      <p>Email: {user.email}</p>
      <p>Phone: {user.phone}</p>
      <p>Website: {user.website}</p>
      <p>Company: {user.company.name}</p>
      <span
        className={
          user.id % 2 === 0 ? 'UserProfile-active' : 'UserProfile-inactive'
        }
      >
        {user.id % 2 === 0 ? 'Active' : 'Inactive'}
      </span>
      <div className="UserProfile-actions">
        <button
          className="UserProfile-edit"
          onClick={() => navigate(`/edit/${user.id}`)}
        >
          Edit
        </button>
        <button className="UserProfile-back" onClick={() => navigate('/')}>
          Back
        </button>
      </div>
    </div>
  );
};

export default UserProfile;
