// src/components/UserDashboard.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import useFetch from '../hooks/useFetch';
import UserList from './UserList';

const UserDashboard = () => {
  const navigate = useNavigate();
  const { data: users, loading, error, refetch } = useFetch(
    'https://jsonplaceholder.typicode.com/users'
  );
  const [showModal, setShowModal] = React.useState(false);
  const [deleteId, setDeleteId] = React.useState(null);

  const handleDelete = async (id) => {
    setDeleteId(id);
    setShowModal(true);
  };

  const confirmDelete = async () => {
    // Simulate API delete (since JSONPlaceholder doesn't actually delete)
    setShowModal(false);
    refetch();
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;

  return (
    <div className="UserDashboard-dashboard">
      <h1>User Dashboard</h1>
      <button
        className="UserDashboard-addButton"
        onClick={() => navigate('/add')}
      >
        Add New User
      </button>
      <UserList users={users} onDelete={handleDelete} />
      {showModal && (
        <div className="UserDashboard-modal">
          <div className="UserDashboard-modalContent">
            <p>Are you sure you want to delete this user?</p>
            <button
              className="UserDashboard-confirm"
              onClick={confirmDelete}
            >
              Yes, Delete
            </button>
            <button
              className="UserDashboard-cancel"
              onClick={() => setShowModal(false)}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
