// src/components/UserCard.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

const UserCard = ({ user, onDelete }) => {
  const navigate = useNavigate();

  return (
    <div className="UserCard-card">
      <img
        src={user.avatar || 'https://via.placeholder.com/70'}
        alt={`${user.name}'s avatar`}
        className="UserCard-avatar"
      />
      <div className="UserCard-info">
        <h3>{user.name}</h3>
        <p>Email: {user.email}</p>
        <p>Role: {user.role}</p>
        <span className={user.status ? 'UserCard-active' : 'UserCard-inactive'}>
          {user.status ? 'Active' : 'Inactive'}
        </span>
      </div>
      <div className="UserCard-actions">
        <button
          className="UserCard-button UserCard-view"
          onClick={() => navigate(`/user/${user.id}`)}
        >
          View
        </button>
        <button
          className="UserCard-button UserCard-edit"
          onClick={() => navigate(`/edit/${user.id}`)}
        >
          Edit
        </button>
        <button
          className="UserCard-button UserCard-delete"
          onClick={() => onDelete(user.id)}
        >
          Delete
        </button>
      </div>
    </div>
  );
};

export default UserCard;
